import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';
import FileBase from 'react-file-base64';

import { createPost, updatePost } from '../../actions/posts';
import useStyles from './styles';

const Form = ({ currentId, setCurrentId }) => {
  const [postData, setPostData] = useState({ title: '',creator: '' , message: '', tags: '', selectedFile: '' });
  const post = useSelector((state) => (currentId ? state.posts.find((message) => message._id === currentId) : null));
  const dispatch = useDispatch();
  const classes = useStyles();
  const user = JSON.parse(localStorage.getItem('profile'));

  useEffect(() => {
    if (post) setPostData(post);
  }, [post]);

  const clear = () => {
    setCurrentId(0);
    setPostData({ title: '', creator: '' ,message: '', tags: '', selectedFile: '' });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (currentId === 0) {
      dispatch(createPost({ ...postData, name: user?.result?.name }));
      clear();
    } else {
      dispatch(updatePost(currentId, { ...postData, name: user?.result?.name }));
      clear();
    }
  };

  if (!user?.result?.name) {
    return (
      <Paper className={classes.paper}>
        <Typography variant="h6" align="center">
          Please Sign In to add your own favourite Book and like other's favourite book.
        </Typography>
      </Paper>
    );
  }

  return (
    // <Paper className={classes.paper}>
    //   <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
    //     <Typography variant="h6">{currentId ? `Editing "${post.title}"` : 'Creating a Memory'}</Typography>
    //     <TextField name="title" variant="outlined" label="Title" fullWidth value={postData.title} onChange={(e) => setPostData({ ...postData, title: e.target.value })} />
    //     <TextField name="message" variant="outlined" label="Message" fullWidth multiline rows={4} value={postData.message} onChange={(e) => setPostData({ ...postData, message: e.target.value })} />
    //     <TextField name="tags" variant="outlined" label="Tags (coma separated)" fullWidth value={postData.tags} onChange={(e) => setPostData({ ...postData, tags: e.target.value.split(',') })} />
    //     <div className={classes.fileInput}><FileBase type="file" multiple={false} onDone={({ base64 }) => setPostData({ ...postData, selectedFile: base64 })} /></div>
    //     <Button className={classes.buttonSubmit} variant="contained" color="primary" size="large" type="submit" fullWidth>Submit</Button>
    //     <Button variant="contained" color="secondary" size="small" onClick={clear} fullWidth>Clear</Button>
    //   </form>
    // </Paper>
    <Paper className={classes.paper}>
       <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
         <Typography variant="h6" style={{color: "white"} }>{currentId ? `Editing ${postData.title}` : 'Adding Book'}</Typography>
         <TextField style={{backgroundColor: "black"}} InputProps={{style: {color: "white"}, classes: {notchedOutline: classes.notchedOutline}}} InputLabelProps={{style:{color: "white"}}} name="title" variant="outlined" label="Title" fullWidth value={postData.title} onChange={(e) => setPostData({ ...postData, title: e.target.value })} />
         <TextField style={{backgroundColor: "black"}} InputProps={{style: {color: "white"}, classes: {notchedOutline: classes.notchedOutline}}} InputLabelProps={{style:{color: "white"}}} name="creator" variant="outlined" label="Creator" fullWidth value={postData.creator} onChange={(e) => setPostData({ ...postData, creator: e.target.value })} />
         <TextField style={{backgroundColor: "black"}} InputProps={{style: {color: "white"}, classes: {notchedOutline: classes.notchedOutline}}} InputLabelProps={{style:{color: "white"}}} name="message" variant="outlined" label="Description" fullWidth multiline rows={3} value={postData.message} onChange={(e) => setPostData({ ...postData, message: e.target.value })} />
         <TextField style={{backgroundColor: "black"}} InputProps={{style: {color: "white"}, classes: {notchedOutline: classes.notchedOutline}}} InputLabelProps={{style:{color: "white"}}} name="tags" variant="outlined" label="Tags (coma separated)" fullWidth value={postData.tags} onChange={(e) => setPostData({ ...postData, tags: e.target.value.split(',') })} />
         <div style={{color: "white"}} className={classes.fileInput}><FileBase type="file" multiple={false} onDone={({ base64 }) => setPostData({ ...postData, selectedFile: base64 })} /></div>
         <Button className={classes.buttonSubmit} variant="contained" color="primary" size="medium" type="submit" fullWidth>Submit</Button>
         <Button className={classes.buttonClear} variant="contained" color="secondary" size="medium" onClick={clear} fullWidth>Clear</Button>
       </form>
     </Paper>
  );
};

export default Form;
